import React, { useState } from "react";
import styles from './scss/chat_page.module.scss'
import axios from "axios";
import { Message } from "../chat_components/message";

const ChatbotApp = () => {
  const [prompt, setPrompt] = useState("");
  const [apiResponse, setApiResponse] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:3001/send-message', {
        messages: [
          {
            role: "user",
            content: prompt
          }
        ],
        model: "gpt-3.5-turbo"
      });

      const answer = [response.data.choices[0].message.content, response.data.created]
      setApiResponse([...apiResponse, answer]);
    } catch (error) {
      console.error(error);
      setApiResponse(["Something went wrong. Please try again."]);
    }
    setLoading(false);
  };

  return (
    <div className={styles.chatPage}>  
	<ul className={styles.messagesList}>
      {apiResponse.map((response, index) => (
        <Message message={response[0]} index={index}/>
        //console.log(response)
        //<div>{response.content}</div>
      ))}
	</ul>
  <form onSubmit={handleSubmit} style={{ marginBottom: '16px', background: 'black' }}>
        <textarea
          type="text"
          value={prompt}
          placeholder="Please ask OpenAI"
          onChange={(e) => setPrompt(e.target.value)}
        ></textarea>
        <button disabled={loading || prompt.length === 0} type="submit">
          {loading ? "Generating..." : "Generate"}
        </button>
      </form>
    </div>
  );
};

export default ChatbotApp;
