import React, { useContext, useState, useEffect } from "react";
import styles from './scss/chat_page.module.scss'
import axios from "axios";
import { Message } from "../chat_components/message";
import send_btn from './images/send_btn.png'
import { ThemeContext } from "../../context/theme_context";

const ChatbotApp = () => {
  const [prompt, setPrompt] = useState("");
  const [apiResponse, setApiResponse] = useState([]);
  const [loading, setLoading] = useState(false);
  const [role, setRole] = useState("Assistant")
  const { darkTheme, toggleTheme } = useContext(ThemeContext);

  useEffect(() => {
    document.documentElement.setAttribute(
      "data-theme",
      darkTheme ? "dark" : "light"
    );
  }, [darkTheme]);

  const handleSubmit = async (e) => {
    e.preventDefault();


    setPrompt('')

    const textarea = document.querySelector('textarea');
  if (textarea) {
    textarea.style.height = '70px';
  }

    const date = new Date();
    const hours = date.getHours();
    const minutes = date.getMinutes();
    const formattedTime = `${hours % 12 || 12}:${minutes < 10 ? '0' : ''}${minutes}`;

    const userMsg = [prompt, formattedTime, 1];
    setApiResponse([...apiResponse, userMsg]);
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:3001/send-message', {
        messages: [
          {
            role: role,
            content: prompt
          }
        ],
        model: "gpt-3.5-turbo"
      });

      const botMsg = [response.data.choices[0].message.content, response.data.created, 0];
      setApiResponse(prevState => [...prevState, botMsg]);
    } catch (error) {
      console.error(error);
      const date = new Date();
      const hours = date.getHours();
      const minutes = date.getMinutes();
      const formattedTime = `${hours % 12 || 12}:${minutes < 10 ? '0' : ''}${minutes}`;
      setApiResponse(prevState => [...prevState, ["Something went wrong. Please try again.", formattedTime, 0]]);
    }

    setLoading(false);
  };
  const handleChangeHeight = (e) => {
    const textarea = e.target;
    textarea.style.height = 0;
    textarea.style.height = textarea.scrollHeight + 'px';
  }


  return (
    <div className={styles.chatPage}>  
  <input className={styles.roleInput} type="text" defaultValue={"Assistant"} 
  onChange={(e) => setRole(e.target.value)}
  />
	<ul className={styles.messagesList}>
      {apiResponse.map((response, index) => (
        <Message message={response[0]} dateTime={response[1]} isUser={response[2]}/>
      ))}
	</ul>
  <form className={styles.formInput} onSubmit={handleSubmit}>
        <section className={styles.inputWrapper}>

        <textarea
          className={styles.inputText}
          type="text"
          value={prompt}
          placeholder="Please ask OpenAI"
          onChange={(e) => { setPrompt(e.target.value); handleChangeHeight(e); }}
          >
        </textarea>
          </section>
        <button className={styles.submitBtn} disabled={loading || prompt.length === 0} type="submit">
          {/*{loading ? "Generating..." : "Generate"}*/}
          {loading ?  
          <div className={styles.wave}>
            <span class={styles.dot}></span>
            <span class={styles.dot}></span>
            <span class={styles.dot}></span>
          </div>
          :
          <img src={send_btn}/> 
          }
        </button>
      </form>
    </div>
  );
};

export default ChatbotApp;
