import { useState } from "react";
import axios from "axios";

//{
//    "messages": [
//        {
//            "role": "user",
//            "content": "Hello, ChatGPT!"
//        }
//    ],
//    "model": "gpt-3.5-turbo"
//}


const ChatbotApp = () => {
  const [prompt, setPrompt] = useState("");
  const [apiResponse, setApiResponse] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:3001/send-message', {  // Update the URL to include port 3001
	  "messages": [
		        {
		            "role": "user",
		            "content": prompt
		        }
		    ],
		    "model": "gpt-3.5-turbo"
      });
    //  setApiResponse(response);
	let answer = response.data.choices[0].message.content;
	setApiResponse(answer)
	setApiResponse()
    } catch (error) {
      console.error(error);
      setApiResponse("Something went wrong. Please try again.");
    }
    setLoading(false);
  };

  return (
    <>
      <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: '100vh' }}>
        <form onSubmit={handleSubmit}>
          <textarea
            type="text"
            value={prompt}
            placeholder="Please ask OpenAI"
            onChange={(e) => setPrompt(e.target.value)}
          ></textarea>
          <button disabled={loading || prompt.length === 0} type="submit">
            {loading ? "Generating..." : "Generate"}
          </button>
        </form>
      </div>
      {apiResponse && (
        <div style={{ display: "flex", justifyContent: "center" }}>
          <pre>
            <strong>API response:</strong>
            {apiResponse}
          </pre>
        </div>
      )}
    </>
  );
};

export default ChatbotApp;
