import styles from './scss/message.module.scss'

export const Message = ({message, dateTime, isUser}) => {
	return (
		<section className={isUser ? styles.messageBodyUser : styles.messageBodyBot}>
          <div>{message}</div>
          <div>{dateTime}</div>
		  <div>{isUser ? 'user' : 'bot'}</div>
        </section>
	)
}
